import React, { useState , useEffect } from 'react'

import Header from './components/Header/Header';
import Footer from './components/Footer/Footer';
import Find from './Pages/Find';
import Serv from './Pages/Serv';
import Discover from './Pages/Discover';
import Work from './Pages/Work';
import LoginSignup from './Pages/LoginSignup';
import LandingPage from './components/LandingPage/LandingPage'; // Corrected path

import { BrowserRouter as Router, Routes, Route, BrowserRouter } from "react-router-dom";

function App() {
  // let path = (window.location.pathname);
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  useEffect(() => {
    const storedLogin = localStorage.getItem('isLoggedIn'); // Check for stored login state
    setIsLoggedIn(storedLogin === 'true');
  }, []);

  const handleLoginSuccess = () => {
    setIsLoggedIn(true);
    localStorage.setItem('isLoggedIn', 'true'); // Persist login state (optional)
  };
    return (
      <>
     <BrowserRouter>
      <Header />
      <Routes>
        <Route path="/" element={<LandingPage />} />
        <Route path="/Find" element={<Find />} />
        <Route path="/Serv" element={<Serv />} />
        <Route path="/Discover" element={<Discover />} />
        <Route path="/Work" element={<Work />} />
        <Route path="/login" element={<LoginSignup />} />
      </Routes>
      <Footer />
    </BrowserRouter>
    </>
  )
}

export default App;
